import { Router } from "express";
import { ordersSummary, ordersCsv, revenueSummary, topPackages, upcomingEvents, pendingAging, ordersTrend, statusDistribution, } from "./report.controller.js";
import { requireAdmin, requireAuth } from "@/middleware/auth.js";
const router = Router();
router.get("/orders/summary", requireAuth, requireAdmin, ordersSummary);
router.get("/orders/status-distribution", requireAuth, requireAdmin, statusDistribution);
router.get("/orders/trend", requireAuth, requireAdmin, ordersTrend);
router.get("/orders/export/csv", requireAuth, requireAdmin, ordersCsv);
router.get("/revenue/summary", requireAuth, requireAdmin, revenueSummary);
router.get("/packages/top", requireAuth, requireAdmin, topPackages);
router.get("/events/upcoming", requireAuth, requireAdmin, upcomingEvents);
router.get("/pending/aging", requireAuth, requireAdmin, pendingAging);
export default router;
